create table syapex.ggops_encryption_profiles (
    deployment_id number,
    profile_id    number generated by default on null as identity not null enable,
    profile_name  varchar2(100 char) not null enable,
    profile_type  varchar2(4000 char) not null enable,
    created       timestamp(6) with local time zone not null enable,
    created_by    varchar2(255 char) not null enable,
    updated       timestamp(6) with local time zone not null enable,
    updated_by    varchar2(255 char) not null enable
);

alter table syapex.ggops_encryption_profiles
    add constraint ggops_encryptio_profile_id_pk primary key ( profile_id )
        using index enable;

alter table syapex.ggops_encryption_profiles add constraint ggops_encryptio_profile_na_unq unique ( profile_name )
    using index enable;

alter table syapex.ggops_encryption_profiles
    add constraint ggops_encryptio_profile_typ_cc
        check ( profile_type in ( 'OKV', 'OCI' ) ) enable;

