create table syapex.ggops_configuration_files (
    process_id    number,
    config_id     number generated by default on null as identity not null enable,
    configuration varchar2(4000 char),
    created       timestamp(6) with local time zone not null enable,
    created_by    varchar2(255 char) not null enable,
    updated       timestamp(6) with local time zone not null enable,
    updated_by    varchar2(255 char) not null enable
);

alter table syapex.ggops_configuration_files
    add constraint ggops_configuratio_config_i_pk primary key ( config_id )
        using index enable;


