create table syapex.ggops_credentials (
    credential_domain_id number,
    user_id              number generated by default on null as identity not null enable,
    user_alias           varchar2(100 char),
    connectuser          varchar2(100 char) not null enable,
    onnectpassword       varchar2(100 char) not null enable,
    created              timestamp(6) with local time zone not null enable,
    created_by           varchar2(255 char) not null enable,
    updated              timestamp(6) with local time zone not null enable,
    updated_by           varchar2(255 char) not null enable
);

alter table syapex.ggops_credentials
    add constraint ggops_credentials_user_id_pk primary key ( user_id )
        using index enable;


