create table syapex.ggops_deployments (
    host_id             number,
    deployment_id       number generated by default on null as identity not null enable,
    deployment_name     varchar2(1000 char) not null enable,
    deployment_title    varchar2(1000 char),
    deployment_label    varchar2(1000 char),
    deployment_platform varchar2(1000 char),
    deployment_ogghome  varchar2(1000 char),
    deployment_etchome  varchar2(1000 char),
    deployment_confhome varchar2(1000 char),
    created             timestamp(6) with local time zone not null enable,
    created_by          varchar2(255 char) not null enable,
    updated             timestamp(6) with local time zone not null enable,
    updated_by          varchar2(255 char) not null enable
);

alter table syapex.ggops_deployments
    add constraint ggops_deploymen_deployment_pk primary key ( deployment_id )
        using index enable;

alter table syapex.ggops_deployments add constraint ggops_deploym_deployment_n_unq unique ( deployment_name )
    using index enable;


