create table syapex.ggops_hosts (
    host_id         number generated by default on null as identity not null enable,
    host_name       varchar2(1000 char) not null enable,
    host_address    varchar2(4000 char) not null enable,
    host_credential varchar2(256 char) not null enable,
    created         timestamp(6) with local time zone not null enable,
    created_by      varchar2(255 char) not null enable,
    updated         timestamp(6) with local time zone not null enable,
    updated_by      varchar2(255 char) not null enable
);

alter table syapex.ggops_hosts
    add constraint ggops_hosts_host_id_pk primary key ( host_id )
        using index enable;

alter table syapex.ggops_hosts add constraint ggops_hosts_host_name_unq unique ( host_name )
    using index enable;


