create table syapex.ggops_identity_domains (
    domain_id                   number generated by default on null as identity not null enable,
    domain_name                 varchar2(64 char) not null enable,
    region                      varchar2(64 char) not null enable,
    tenant_ocid                 varchar2(256 char) not null enable,
    administrator_ocid          varchar2(256 char) not null enable,
    adminisator_key_fingerprint varchar2(256 char) not null enable,
    adminstrator_private_key    varchar2(4000 char) not null enable,
    created                     timestamp(6) with local time zone not null enable,
    created_by                  varchar2(255 char) not null enable,
    updated                     timestamp(6) with local time zone not null enable,
    updated_by                  varchar2(255 char) not null enable
);

alter table syapex.ggops_identity_domains add constraint ggops_identity_domain_nam_unq unique ( domain_name )
    using index enable;

alter table syapex.ggops_identity_domains
    add constraint ggops_identity_dom_domain_i_pk primary key ( domain_id )
        using index enable;


