create table syapex.ggops_paths (
    process_id       number,
    path_id          number generated by default on null as identity not null enable,
    path_name        varchar2(100 char) not null enable,
    source           number,
    source_uri       varchar2(4000 char),
    target           number,
    target_uri       varchar2(4000 char),
    compression_char varchar2(4000 char) default 'N',
    created          timestamp(6) with local time zone not null enable,
    created_by       varchar2(255 char) not null enable,
    updated          timestamp(6) with local time zone not null enable,
    updated_by       varchar2(255 char) not null enable
);

alter table syapex.ggops_paths
    add constraint ggops_paths_path_id_pk primary key ( path_id )
        using index enable;


