create table syapex.ggops_processes (
    deployment_id      number,
    process_id         number generated by default on null as identity not null enable,
    process_name       varchar2(100 char) not null enable,
    process_type       varchar2(100 char) not null enable,
    process_trail      varchar2(2 char) not null enable,
    process_trail_size number(*, 0) default '500',
    process_begin      varchar2(100 char) default 'NOW',
    process_intent     varchar2(100 char) default 'UNIDIRECTIONAL',
    encryption_profile number,
    credential_id      number,
    created            timestamp(6) with local time zone not null enable,
    created_by         varchar2(255 char) not null enable,
    updated            timestamp(6) with local time zone not null enable,
    updated_by         varchar2(255 char) not null enable
);

alter table syapex.ggops_processes
    add constraint ggops_processes_process_id_pk primary key ( process_id )
        using index enable;

alter table syapex.ggops_processes
    add constraint ggops_processes_process_typ_cc
        check ( process_type in ( 'EXTRACT', 'REPLICAT', 'SOURCE', 'TARGET' ) ) enable;


